mapper.select:
select
o.id, o.type_id, o.user_id,o.user_name,o.open_id, o.user_tel, o.call_time, o.status, o.state,o.order_type, o.flight, o.airport, o.address, o.destination,
o.use_time, o.airport_pickup_time, o.appointment_time, o.order_time, o.use_car_type, o.number,
o.is_visit, o.need_order, o.visitor, o.visit_time, o.order_service, o.is_order, o.completed, o.expect_money,
o.reality_money, o.order_no, o.record_no, o.user_addr, o.dealer_addr, o.contact_name,
o.contact_tel, o.is_credit, o.id_no, o.driver_no,o.rent_city, o.order_car_type, o.store_name,
o.store_addr,o.return_name, o.return_tel,o.begin_time, o.end_time, o.duration, o.service_provider,o.record_agent, o.order_tel, o.is_gl, o.is_replace,
o.replace_car, o.actual_car, o.create_time, o.update_time, o.update_by, o.create_by, o.remark,
u.id as u_id, u.vin, u.user_name as c_name, u.open_id as c_openid, u.phone, u.service_card, u.service_type, u.car_type,
u.car_license, u.report_time,u.due_time,u.due_count, u.a_total_count, u.a_total_used, u.a_current_count, u.p_total_count,
u.p_total_used,u.p_current_count,u.r_total_count, u.r_total_used,u.r_current_count,u.vin_type,t.type, t.name
from orders o left join users u on o.user_id = u.id
left join types t on o.type_id = t.type
where 1=1

  AND o.id= #{id}


  AND o.type_id= #{typeId}


  AND o.order_type= #{orderType}


  AND u.service_card= #{user.serviceCard}


  AND o.user_name= #{userName}


  AND o.user_tel= #{userTel}


  AND o.use_car_type= #{useCarType}


  AND u.vin= #{user.vin}


  AND o.is_order= #{isOrder}


  AND o.completed= #{completed}


  AND o.state = #{state}


  AND o.order_no= #{orderNo}


  AND u.car_type= #{user.carType}


  AND o.flight= #{flight}


  AND u.car_license= #{user.carLicense}


  AND o.service_provider= #{serviceProvider}


  AND o.rent_city= #{rentCity}


  AND o.use_time >= #{useTimeStart}


  AND o.use_time <= #{useTimeEnd}


  AND u.report_time >= #{reportTimeStart}


  AND u.report_time <= #{reportTimeEnd}


  AND o.order_time >= #{orderTimeStart}


  AND o.order_time <= #{orderTimeEnd}


  AND o.appointment_time >= #{appointmentTimeStart}


  AND o.appointment_time <= #{appointmentTimeEnd}


  AND o.begin_time >= #{beginTimeStart}


  AND o.begin_time <= #{beginTimeEnd}


  AND o.end_time >= #{endTimeStart}


  AND o.end_time <= #{endTimeEnd}


  ORDER by o.use_time desc


 ORDER by o.begin_time desc

select

from orders
where order_no = #{orderNo,jdbcType=VARCHAR} and type_id= #{typeId,jdbcType=INTEGER}

select count(1) from
orders o left JOIN users u on o.user_id = u.id
where u.vin = #{vin,jdbcType=VARCHAR} and o.type_id = #{typeId, jdbcType=INTEGER}
and o.completed = '1'

  and o.use_time >= #{startTime,jdbcType=TIMESTAMP} and o.use_time <= #{endTime,jdbcType=TIMESTAMP}


  and o.end_time >= #{startTime,jdbcType=TIMESTAMP} and o.end_time <= #{endTime,jdbcType=TIMESTAMP}

select u.vin,o.end_time,o.use_time,count(1)as count from orders o left join users u on o.user_id = u.id left join types t on o.type_id = t.type where o.completed = '1' and o.type_id = #{typeId, jdbcType=INTEGER} and u.vin= #{vin,jdbcType=VARCHAR}
GROUP BY DATE_FORMAT(o.use_time, '%Y%m%d') ORDER BY o.use_time ASC
GROUP BY DATE_FORMAT(o.end_time, '%Y%m%d') ORDER BY o.end_time ASC

SELECT use_time, end_time, count(*) as total, SUM(case when state = 2 then 1 else 0 end) as completed, sum(case when state = 3 and is_order = 1 then 1 else 0 end) as cancel, sum(case when state = 1 then 1 else 0 end) as handle, sum(case when order_type = 1 then 1 else 0 end) as person, sum(case when order_type = 2 then 1 else 0 end) as wechat, sum(if(state = 2, reality_money, 0)) as money from orders where type_id = #{typeId}
GROUP BY DATE_FORMAT(use_time, '%Y') ORDER BY use_time ASC
AND end_time is not null GROUP BY DATE_FORMAT(end_time, '%Y') ORDER BY end_time ASC

SELECT o.use_time, o.end_time, u.vin, COUNT(*) as count FROM orders o LEFT JOIN users u ON o.user_id = u.id WHERE o.completed = 1 AND o.type_id = #{typeId} AND u.car_type = #{carType}
GROUP BY u.vin, DATE_FORMAT(o.use_time, '%Y') ORDER BY o.use_time ASC
GROUP BY u.vin, DATE_FORMAT(o.end_time, '%Y') ORDER BY o.end_time ASC

select

from orders
where 1 = 1

  and user_id = #{userId}


  and use_time = #{useTime}


  and begin_time = #{beginTime}


  and end_time = #{endTime}
